import React, { useEffect, useState } from 'react';
import M from 'materialize-css';
import './main.css'
import { Icon } from '@mdi/react';
import { mdiFilter } from '@mdi/js';
import ReactChipInput from "react-chip-input";

// Components
import { Header } from './Components/Header'
import { CentralBox } from './Components/CentralBox';
import { List, Item } from './Components/List'

var instances

export function MainPage(props) {

    useEffect(() => {
        var elem = document.querySelectorAll('.chips')
        instances = M.Chips.init(elem)
    }, [])

    const [dropDown, setDropDown] = useState('none')
    const [chips, setChips] = useState([])

    const addChip = value => {
        const temp = chips.slice();
        temp.push(value);
        setChips(temp);
    }

    const removeChip = index => {
        const temp = chips.slice();
        temp.splice(index, 1);
        setChips(temp);
    }

    const dropDownList = () => {
        if (dropDown == 'none') {
            setDropDown('flex')
        } else {
            setDropDown('none')
        }
    }

    return (
        <div>
            <Header title="Sistema de Alimentos" />
            <CentralBox title="Alimentos">
                <div className="section">
                    <div className="form-row">
                        <input placeholder="Search..."/>
                        <div className="drop-down">
                            <button className="drop-down-btn" onClick={dropDownList}>
                                <Icon 
                                    path={mdiFilter}
                                    size={1}
                                        />
                            </button>
                            <div className="drop-down-menu" style={{display: dropDown}}>
                                aa
                            </div>
                        </div>
                    </div>
                    <List>
                        <Item index={1} name="MacarrÃ£o" />
                    </List>
                </div>
                <div className="section">
                    <div className="underline-header">
                        Adicionar Alimento
                    </div>
                    <div className="form-row">
                        <label htmlFor="nome">Nome:</label>
                        <input id="nome"/>
                    </div>
                    <label>Ingredientes:</label>
                    <div className="chips chips-placeholder ing-chips">
                        <input style={{width: "10px"}} placeholder="Digite um ingrediente e pressione Enter!" />
                    </div>
                    <div className="form-row">
                        <ReactChipInput
                            classes="class1 class2"
                            chips={chips}
                            onSubmit={value => addChip(value)}
                            onRemove={index => removeChip(index)}
                        />

                    </div>
                    <button className="submit-btn">
                        Salvar
                    </button>
                </div>
            </CentralBox>
        </div>
    )
}
