import React, { useEffect, useState } from 'react';
import M from 'materialize-css';
import './main.css'
import { Icon } from '@mdi/react';
import { mdiFilter } from '@mdi/js';
import ReactChipInput from "react-chip-input";

// Components
import { Header } from './Components/Header'
import { CentralBox } from './Components/CentralBox';
import { List, Item } from './Components/List'
import axios from 'axios';

export function MainPage(props) {

    useEffect(() => {
        var elem = document.querySelectorAll('.chips')
        M.Chips.init(elem)
    }, [])

    useEffect(() => {
        axios.get('http://localhost:3000/ingredientes').then(res => {
            console.log(res);
        });
    })

    const [dropDown, setDropDown] = useState('none')
    const [chips, setChips] = useState([])
    const [alimentos, setAlimentos] = useState([])

    const dropDownList = () => {
        if (dropDown === 'none') {
            setDropDown('flex')
        } else {
            setDropDown('none')
        }
    }

    return (
        <div>
            <Header title="Sistema de Alimentos" />
            <CentralBox title="Alimentos">
                <div className="section">
                    <div className="form-row">
                        <input placeholder="Search..."/>
                        <div className="drop-down">
                            <button className="drop-down-btn" onClick={dropDownList}>
                                <Icon 
                                    path={mdiFilter}
                                    size={1}
                                        />
                            </button>
                            <div className="drop-down-menu" style={{display: dropDown}}>
                                <select>
                                    <option>opção</option>
                                </select>
                                <input />
                            </div>
                        </div>
                    </div>
                    <List>
                        <Item index={1} name="Macarrão" />
                    </List>
                </div>
                <div className="section">
                    <div className="underline-header">
                        Adicionar Alimento
                    </div>
                    <div className="form">
                        <div className="form-row">
                            <label htmlFor="nome">Nome:</label>
                            <input id="nome"/>
                        </div>
                        <label>Ingredientes:</label>
                        <div className="chips ing-chips">
                            <input style={{width: "10px"}} />
                        </div>
                        <small>Digite um ingrediente e pressione Enter!</small>
                    </div>

                    <button className="submit-btn">
                        Salvar
                    </button>
                </div>
            </CentralBox>
        </div>
    )
}
