import React, { useEffect, useState } from 'react';
import M from 'materialize-css';
import './main.css'
import { Icon } from '@mdi/react';
import { mdiFilter } from '@mdi/js';

// Components
import { Header } from './Components/Header'
import { CentralBox } from './Components/CentralBox';
import { List, Item } from './Components/List'
import axios from 'axios';

var instances = []

export function MainPage(props) {

    useEffect(() => {
        var elem = document.querySelectorAll('.chips')
        instances = M.Chips.init(elem)
    }, [])

    useEffect(() => {
        axios.get('http://localhost:8080/alimentos').then(res => {
            setAlimentos(res.data)
        });
    }, [])

    const [alimentos, setAlimentos] = useState([])
    const [nome, setNome] = useState("")

    const handleNome = (e) => {
        setNome(e.target.value)
    }

    const handleCreateAlimento = () => {
        // Recupera os ingredientes
        let chipsData = instances[0].chipsData
        let chips = []
        chipsData.forEach(chip => {
            chips.push(chip.tag)
        })

        let json = {
            nome: nome,
            ingredientes: chips
        }

        axios.post("http://localhost:8080/alimentos/create", json).then(res => {
            axios.get('http://localhost:8080/alimentos').then(res => {
                setAlimentos(res.data)
            });
        })

        // Limpa o formulÃ¡rio
        setNome("")
        instances[0].chipsData = []
        document.querySelectorAll(".chip").forEach(elem => {
            elem.remove()
        })
    }

    const handleDeleteAlimento = (id) => {
        if (window.confirm("Deseja excluir este alimento?")) {
            axios.delete("http://localhost:8080/alimentos/delete/" + id).then(res => {
                axios.get('http://localhost:8080/alimentos').then(res => {
                    setAlimentos(res.data)
                });
            })
        }
    }

    return (
        <div>
            <Header title="Sistema de Alimentos" />
            <CentralBox title="Alimentos">

                <AlimentosList alimentos={alimentos} handleDelete={handleDeleteAlimento} />

                <div className="section">
                    <div className="underline-header">
                        Adicionar Alimento
                    </div>
                    <div className="form">
                        <div className="form-row">
                            <label htmlFor="nome">Nome:</label>
                            <input id="nome" onChange={handleNome} value={nome} />
                        </div>
                        <label>Ingredientes:</label>
                        <div className="chips ing-chips">
                            <input />
                        </div>
                        <small>Digite um ingrediente e pressione Enter!</small>
                    </div>

                    <button className="submit-btn" onClick={handleCreateAlimento}>
                        Salvar
                    </button>
                </div>
            </CentralBox>
        </div>
    )
}

function AlimentosList(props) {
    const { alimentos, handleDelete } = props
    const [search, setSearch] = useState("")
    const [dropDown, setDropDown] = useState('none')

    const originalData = alimentos

    const filter = () => {
        let newData = []
        if (search.length === 0) {
            return originalData
        } else {
            for (let i = 0; i < originalData.length; i++) {
                let nome = originalData[i].nome.toUpperCase()
                if (nome.includes(search.toUpperCase())) {
                    newData.push(originalData[i])
                }
                return newData
            }
        }
    }

    var data


    const handleSearch = (e) => {
        setSearch(e.target.value)
    }

    const dropDownList = () => {
        if (dropDown === 'none') {
            setDropDown('flex')
        } else {
            setDropDown('none')
        }
    }

    return (
        <div className="section">
                    <div className="form-row">
                        <input placeholder="Search..." value={search} onChange={handleSearch} />
                        <div className="drop-down">
                            <button className="drop-down-btn" onClick={dropDownList}>
                                <Icon 
                                    path={mdiFilter}
                                    size={1}
                                        />
                            </button>
                            <div className="drop-down-menu" style={{display: dropDown}}>
                                <form>
                                    <label>
                                        <input name="group1" type="radio" defaultChecked />
                                        <span>ContÃ©m</span>
                                    </label>
                                    <label>
                                        <input name="group1" type="radio" />
                                        <span>NÃ£o contÃ©m</span>
                                    </label>
                                </form>
                                <input />
                            </div>
                        </div>
                    </div>
                    <List>
                        { data.map((row, index) => (
                            <Item index={index+1} obj={row} handleDelete={() => handleDelete(row.id)} key={row.id} />
                        )) }
                    </List>
                </div>
    )
}
