import React, { useEffect } from 'react'
import M from 'materialize-css';
import { Icon } from '@mdi/react';
import { mdiClose } from '@mdi/js';

var instances = []

export class Modal2 extends React.Component {

    constructor(props) {
        super(props);
        this.state = {alimento: this.props.alimento};
    }
    componentDidMount() {
        let data = []
        let ingredientes = this.state.alimento.ingredientes
        ingredientes.forEach(ing => {
            data.push({tag: ing})
        })
        let options = {
            data: data
        }
        var elem = document.querySelectorAll('#chips')
        instances = M.Chips.init(elem, options)
    }
    render() {
        if (this.props.modal) {
            return (
                <div className="modal-container">
                    <div className="modal-box">
                        <div className="modal-header">
                            <div className="underline-header">
                                Alimento
                            </div>
                            <div className="close" onClick={this.props.close}>
                                <Icon path={mdiClose} size={1} />
                            </div>
                        </div>
                        <div className="form-row">
                            <label>Nome:</label>
                            <input value={this.props.alimento.nome} disabled={true} />
                        </div>
                        <label>Ingredientes:</label>
                        <div className="chips ing-chips" id="chips">
                            <input />
                        </div>
                    </div>
                </div>
            )
        } else {
            return (<div></div>)
        }
    }
}
