import React, { useEffect, useState } from 'react';
import M from 'materialize-css';
import './main.css'
import { Icon } from '@mdi/react';
import { mdiFilter } from '@mdi/js';

// Components
import { Header } from './Components/Header'
import { CentralBox } from './Components/CentralBox';
import { List, Item } from './Components/List'
import axios from 'axios';


export function MainPage(props) {
    const addChip = () => {
        console.log("ASDMASDMKA")
    }

    useEffect(() => {
        var options = {
            onChipAdd: () => {
                let chipsData = instances[0].chipsData
                let chips = []
                chipsData.forEach(chip => {
                    chips.push(chip.tag)
                })
                setIngredientes(chips)
                console.log(chips)
                console.log(ingredientes)
            },
            onChipDelete: () => {

            },
            placeholder: "Ingredientes"
        }

        var elem = document.querySelectorAll('.chips')
        var instances = M.Chips.init(elem, options)
    }, [])

    useEffect(() => {
        axios.get('http://localhost:8080/alimentos').then(res => {
            setAlimentos(res.data)
        });
    }, [])

    const [dropDown, setDropDown] = useState('none')
    const [ingredientes, setIngredientes] = useState()
    const [alimentos, setAlimentos] = useState([])


    const dropDownList = () => {
        if (dropDown === 'none') {
            setDropDown('flex')
        } else {
            setDropDown('none')
        }
    }


    return (
        <div>
            <Header title="Sistema de Alimentos" />
            <CentralBox title="Alimentos">
                <div className="section">
                    <div className="form-row">
                        <input placeholder="Search..."/>
                        <div className="drop-down">
                            <button className="drop-down-btn" onClick={dropDownList}>
                                <Icon 
                                    path={mdiFilter}
                                    size={1}
                                        />
                            </button>
                            <div className="drop-down-menu" style={{display: dropDown}}>
                                <form>
                                    <label>
                                        <input name="group1" type="radio" defaultChecked />
                                        <span>Contém</span>
                                    </label>
                                    <label>
                                        <input name="group1" type="radio" />
                                        <span>Não contém</span>
                                    </label>
                                </form>
                                <input />
                            </div>
                        </div>
                    </div>
                    <List>
                        { alimentos.map((row, index) => (
                            <Item index={index+1} name={row.nome} key={row.id} />
                        )) }
                    </List>
                </div>
                <div className="section">
                    <div className="underline-header">
                        Adicionar Alimento
                    </div>
                    <div className="form">
                        <div className="form-row">
                            <label htmlFor="nome">Nome:</label>
                            <input id="nome"/>
                        </div>
                        <label>Ingredientes:</label>
                        <div className="chips ing-chips">
                            <input />
                        </div>
                        <small>Digite um ingrediente e pressione Enter!</small>
                    </div>

                    <button className="submit-btn">
                        Salvar
                    </button>
                </div>
            </CentralBox>
        </div>
    )
}
