import React, { useEffect } from 'react'
import M from 'materialize-css';
import { Icon } from '@mdi/react';
import { mdiClose } from '@mdi/js';

var instances = []

export function Modal(props) {
    const { close, modal, alimento } = props

    useEffect(() => {
        let data = []
        let ingredientes = alimento.ingredientes
        ingredientes.forEach(ing => {
            data.push({tag: ing})
        })
        let options = {
            data: data
        }
        var elem = document.querySelectorAll('#chips')
        instances = M.Chips.init(elem, options)
    }, [])

    if (modal) {
        console.log(alimento)
        return (
            <div className="modal-container">
                <div className="modal-box">
                    <div className="modal-header">
                        <div className="underline-header">
                            Alimento
                        </div>
                        <div className="close" onClick={close}>
                            <Icon path={mdiClose} size={1} />
                        </div>
                    </div>
                    <div className="form-row">
                        <label>Nome:</label>
                        <input value={alimento.nome} disabled={true} />
                    </div>
                    <label>Ingredientes:</label>
                    <div className="chips ing-chips" id="chips">
                        <input />
                    </div>
                </div>
            </div>
        )
    } else {
        return (<div></div>)
    }
}
